function address_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('address');
	if(!$arrPerm[1]){
		return false;
	}

	$data['addressType'] = makeSafe($_REQUEST['addressType']);
		if($data['addressType'] == empty_lookup_value){ $data['addressType'] = ''; }
	$data['addressLine1'] = makeSafe($_REQUEST['addressLine1']);
		if($data['addressLine1'] == empty_lookup_value){ $data['addressLine1'] = ''; }
	$data['addressLine2'] = makeSafe($_REQUEST['addressLine2']);
		if($data['addressLine2'] == empty_lookup_value){ $data['addressLine2'] = ''; }
	$data['city'] = makeSafe($_REQUEST['city']);
		if($data['city'] == empty_lookup_value){ $data['city'] = ''; }
	$data['state'] = makeSafe($_REQUEST['state']);
		if($data['state'] == empty_lookup_value){ $data['state'] = ''; }
	$data['zip'] = makeSafe($_REQUEST['zip']);
		if($data['zip'] == empty_lookup_value){ $data['zip'] = ''; }

	// hook: address_before_insert
	if(function_exists('address_before_insert')){
		$args=array();
		if(!address_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `address` set       `addressType`=' . (($data['addressType'] !== '' && $data['addressType'] !== NULL) ? "'{$data['addressType']}'" : 'NULL') . ', `addressLine1`=' . (($data['addressLine1'] !== '' && $data['addressLine1'] !== NULL) ? "'{$data['addressLine1']}'" : 'NULL') . ', `addressLine2`=' . (($data['addressLine2'] !== '' && $data['addressLine2'] !== NULL) ? "'{$data['addressLine2']}'" : 'NULL') . ', `city`=' . (($data['city'] !== '' && $data['city'] !== NULL) ? "'{$data['city']}'" : 'NULL') . ', `state`=' . (($data['state'] !== '' && $data['state'] !== NULL) ? "'{$data['state']}'" : 'NULL') . ', `zip`=' . (($data['zip'] !== '' && $data['zip'] !== NULL) ? "'{$data['zip']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"address_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: address_after_insert
	if(function_exists('address_after_insert')){
		$res = sql("select * from `address` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!address_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('address', $recID, getLoggedMemberID());

	return $recID;
}

function address_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
