function email_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('email');
	if(!$arrPerm[1]){
		return false;
	}

	$data['emailType'] = makeSafe($_REQUEST['emailType']);
		if($data['emailType'] == empty_lookup_value){ $data['emailType'] = ''; }
	$data['emailAddress'] = makeSafe($_REQUEST['emailAddress']);
		if($data['emailAddress'] == empty_lookup_value){ $data['emailAddress'] = ''; }
	$data['private'] = makeSafe($_REQUEST['private']);
		if($data['private'] == empty_lookup_value){ $data['private'] = ''; }

	// hook: email_before_insert
	if(function_exists('email_before_insert')){
		$args=array();
		if(!email_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `email` set       `emailType`=' . (($data['emailType'] !== '' && $data['emailType'] !== NULL) ? "'{$data['emailType']}'" : 'NULL') . ', `emailAddress`=' . (($data['emailAddress'] !== '' && $data['emailAddress'] !== NULL) ? "'{$data['emailAddress']}'" : 'NULL') . ', `private`=' . (($data['private'] !== '' && $data['private'] !== NULL) ? "'{$data['private']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"email_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: email_after_insert
	if(function_exists('email_after_insert')){
		$res = sql("select * from `email` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!email_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('email', $recID, getLoggedMemberID());

	return $recID;
}

function email_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('email');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='email' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='email' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: email_before_delete
	if(function_exists('email_before_delete')){
		$args=array();
		if(!email_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `email` where `id`='$selected_id'", $eo);

	// hook: email_after_delete
	if(function_exists('email_after_delete')){
		$args=array();
		email_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='email' and pkValue='$selected_id'", $eo);
}

function email_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('email');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='email' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='email' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['emailType'] = makeSafe($_REQUEST['emailType']);
		if($data['emailType'] == empty_lookup_value){ $data['emailType'] = ''; }
	$data['emailAddress'] = makeSafe($_REQUEST['emailAddress']);
		if($data['emailAddress'] == empty_lookup_value){ $data['emailAddress'] = ''; }
	$data['private'] = makeSafe($_REQUEST['private']);
		if($data['private'] == empty_lookup_value){ $data['private'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: email_before_update
	if(function_exists('email_before_update')){
		$args=array();
		if(!email_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `email` set       `emailType`=' . (($data['emailType'] !== '' && $data['emailType'] !== NULL) ? "'{$data['emailType']}'" : 'NULL') . ', `emailAddress`=' . (($data['emailAddress'] !== '' && $data['emailAddress'] !== NULL) ? "'{$data['emailAddress']}'" : 'NULL') . ', `private`=' . (($data['private'] !== '' && $data['private'] !== NULL) ? "'{$data['private']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="email_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: email_after_update
	if(function_exists('email_after_update')){
		$res = sql("SELECT * FROM `email` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!email_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='email' and pkValue='".makeSafe($selected_id)."'", $eo);

}

